import { beforeEach, describe, expect, it } from "vitest";
import { postgresStorage } from "./postgresStorage";
import { getTables } from "./getTables";
import { PgDatabase, QueryResultHKT } from "drizzle-orm/pg-core";
import { DefaultLogger } from "drizzle-orm";
import { createTable } from "./createTable";
import { drizzle } from "drizzle-orm/postgres-js";
import { createPublicClient, http } from "viem";
import { foundry } from "viem/chains";
import { blockLogsToStorage } from "../blockLogsToStorage";
import postgres from "postgres";

describe("postgresStorage", async () => {
  let db: PgDatabase<QueryResultHKT>;
  const getSchemaName = (schemaName: string): string => `${process.pid}_${process.env.VITEST_POOL_ID}__${schemaName}`;

  const publicClient = createPublicClient({
    chain: foundry,
    transport: http(),
  });

  beforeEach(async () => {
    db = drizzle(postgres(process.env.DATABASE_URL!), {
      logger: new DefaultLogger(),
    });
  });

  it("should create tables and data from block log", async () => {
    const adapter = await postgresStorage({ database: db, publicClient, getSchemaName });

    await blockLogsToStorage(adapter)({
      blockNumber: 5448n,
      logs: [
        {
          address: "0x5fbdb2315678afecb367f032d93f642f64180aa3",
          topics: ["0xd01f9f1368f831528fc9fe6442366b2b7d957fbfff3bcf7c24d9ab5fe51f8c46"],
          data: "",
          blockHash: "0x4ad3752c86f900332e0d2d8903480e7206747d233586574d16f006eebdb5138b",
          blockNumber: 2n,
          transactionHash: "0xaa54bf18053cce5d4d2906538a60cb1d9958cc3c10c34b5f9fdc92fe6a6abab4",
          transactionIndex: 16,
          logIndex: 54,
          removed: false,
          args: {
            table: "0x000000000000000000000000000000005265736f757263655479706500000000",
            key: ["0x00000000000000000000000000000000496e76656e746f727900000000000000"],
            schemaIndex: 0,
            data: "0x02",
          },
          eventName: "StoreSetField",
        },
        {
          address: "0x5fbdb2315678afecb367f032d93f642f64180aa3",
          topics: ["0x912af873e852235aae78a1d25ae9bb28b616a67c36898c53a14fd8184504ee32"],
          data: "",
          blockHash: "0x4ad3752c86f900332e0d2d8903480e7206747d233586574d16f006eebdb5138b",
          blockNumber: 2n,
          transactionHash: "0xaa54bf18053cce5d4d2906538a60cb1d9958cc3c10c34b5f9fdc92fe6a6abab4",
          transactionIndex: 16,
          logIndex: 55,
          removed: false,
          args: {
            table: "0x6d756473746f726500000000000000005461626c657300000000000000000000",
            key: ["0x00000000000000000000000000000000496e76656e746f727900000000000000"],
            data: "",
          },
          eventName: "StoreSetRecord",
        },
      ],
    });

    expect(await db.select().from(adapter.internalTables.chain)).toMatchInlineSnapshot(`
      [
        {
          "chainId": 31337,
          "lastError": null,
          "lastUpdatedBlockNumber": 5448n,
          "schemaVersion": 1,
        },
      ]
    `);

    expect(await db.select().from(adapter.internalTables.tables)).toMatchInlineSnapshot(`
      [
        {
          "address": "0x5FbDB2315678afecb367f032d93F642f64180aa3",
          "id": "0x5FbDB2315678afecb367f032d93F642f64180aa3::Inventory",
          "keySchema": {
            "item": "uint32",
            "itemVariant": "uint32",
            "owner": "address",
          },
          "lastError": null,
          "lastUpdatedBlockNumber": 5448n,
          "name": "Inventory",
          "namespace": "",
          "schemaVersion": 1,
          "tableId": "0x00000000000000000000000000000000496e76656e746f727900000000000000",
          "valueSchema": {
            "amount": "uint32",
          },
        },
      ]
    `);

    const tables = await getTables(db, [], getSchemaName);
    expect(tables).toMatchInlineSnapshot(`
      [
        {
          "address": "0x5FbDB2315678afecb367f032d93F642f64180aa3",
          "id": "0x5FbDB2315678afecb367f032d93F642f64180aa3::Inventory",
          "keySchema": {
            "item": "uint32",
            "itemVariant": "uint32",
            "owner": "address",
          },
          "lastUpdatedBlockNumber": 5448n,
          "name": "Inventory",
          "namespace": "",
          "tableId": "0x00000000000000000000000000000000496e76656e746f727900000000000000",
          "valueSchema": {
            "amount": "uint32",
          },
        },
      ]
    `);

    const table = tables[0];
    const sqlTable = createTable({ ...table, getSchemaName });
    expect(await db.select().from(sqlTable)).toMatchInlineSnapshot("[]");

    await adapter.cleanUp();
  });
});
